mapper.select:
select * from res_data where 1=1
and name like '%'||#{bookname}||'%'
and author = #{userId}
and state != ${@com.xiaomy.book_city.app.constant.StateType@REMOVE}
order by order_by

mapper.update:
update res_data set
state = ${@com.xiaomy.book_city.app.constant.StateType@REMOVE}
where id = #{bookId}

update res_data
bookname = #{bookname}, author = #{author}, updatetime = #{updatetime}, synopsis = #{synopsis}, classify = #{classify}, cover = #{cover}, price = #{price}
where id = #{id}
